    return float(
        np.minimum(
            max_unit_cost_of_gas_exploration(),
            if_then_else(
                productivity_of_investment_in_gas_exploration() == 0,
                lambda: 0,
                lambda: 1
                / productivity_of_investment_in_gas_exploration()
                * toe_per_mtoe(),
            ),
        )
    )


